R , TB_INSP_ITEM_DTL D WHERE 1=1 AND R.INSP_CD=D.INSP_CD AND R.INSP_TYPE=D.INSP_TYPE AND R.INSP_SEQ=D.INSP_SEQ AND INSP_RESULT_GUBUN='002' AND INSP_SOURCE_NO=A.WORK_ORD_NO AND INSPECT_GUBUN='003' GROUP BY D.INSP_CD)T),'NG'))APPROVAL_JONG, W.MD_INSPECT_YN FROM TB_WORK_ORDER_PRCSS A LEFT OUTER JOIN TB_WORK_ORDER W ON A.WORK_ORD_NO=W.WORK_ORD_NO LEFT OUTER JOIN TB_ITEM_INFO_ADM B ON A.ITEM_SEQ=B.ITEM_SEQ LEFT OUTER JOIN TB_LOCATION_ADM C ON C.LOC_NO = B.LOC_NO WHERE 1=1 AND A.WORK_ORD_NO = #{workOrdNo} AND B.ITEM_SEQ = #{itemSeq}
