AND BOD.CONT_DTL_NO = '999999999999999'
AND BOD.CONT_DTL_NO = #{contDtlNo}
AND WOA.WORK_ORD_NO = #{workOrdNo}
AND WOM.BARCODE_NO = #{barcodeNo}

