SELECT WOP.WORK_ORD_NO , WOP.ORD_LOT_NO , WOP.WORK_STATUS , WOP.WORK_GUBUN , (SELECT X.BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_CD = WOP.WORK_GUBUN AND X.BASE_GROUP_CD = '114') WORK_GUBUN_NM , WOP.WORK_CHARGR , (SELECT X.USER_NM FROM TB_USER X WHERE X.USER_NUMBER = WOP.WORK_CHARGR) WORK_CHARGR_NM , WOP.INSPECT_GUBUN , (SELECT X.BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_CD = WOP.INSPECT_GUBUN AND X.BASE_GROUP_CD = '118') INSPECT_GUBUN_NM , WOP.FAIR_QTY , WOP.WORK_END_TIME , ICMA.INSPECT_DATE , IWA.OUT_DATE , IWA.OUT_QTY , IWA.REMAIN_QTY , DOD.PO_CORP_CD , (SELECT X.DEAL_CORP_NM FROM TB_DEAL_CORP_ADM X WHERE X.DEAL_CORP_CD = DOD.PO_CORP_CD) PO_CORP_NM , IIA.ITEM_MODEL , (SELECT X.BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_CD = IIA.ITEM_MODEL AND X.BASE_GROUP_CD = '099') ITEM_MODEL_NM , IIA.ITEM_CD , IIA.ITEM_NM , ICMA.INSPECT_DATE OUTPUT_INSPECT_DATE , DOD.OUTPUT_DATE , DOD.OUTPUT_QTY , IIA.MAIN_GUBUN FROM TB_WORK_ORDER_PRCSS WOP LEFT OUTER JOIN TB_DELIVERY_ORDER_DTL DOD ON WOP.ORD_LOT_NO = DOD.LOT_NO LEFT OUTER JOIN TB_ITEM_COMPLETE_MASTER_ADM ICMA ON DOD.DO_NO = ICMA.DO_NO AND DOD.DO_NO_SEV = ICMA.DO_NO_SUB LEFT OUTER JOIN TB_ITEM_WHS_ADM IWA ON WOP.ORD_LOT_NO = IWA.BARCODE_NO LEFT OUTER JOIN TB_ITEM_INFO_ADM IIA ON WOP.ITEM_SEQ = IIA.ITEM_SEQ LEFT OUTER JOIN TB_ITEM_PRCSS_COMPLETE_ADM IPCA ON WOP.WORK_ORD_NO = IPCA.WORK_ORD_NO AND CONVERT(CHAR(8), WOP.WORK_END_TIME, 112) = IPCA.WORK_END_TIME LEFT OUTER JOIN TB_DELIVERY_PROC_DTL DPD ON DOD.DO_NO = DPD.DO_NO AND DOD.DO_SEQ = DPD.DO_SEQ WHERE 1=1
