SELECT T2.ITEM_SEQ , T2.ITEM_CD , T2.ITEM_NM , T2.ITEM_MODEL , (SELECT X.BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_CD = T2.ITEM_MODEL AND X.BASE_GROUP_CD = '099') ITEM_MODEL_NM , T2.LOCATION_NO , (SELECT X.BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_GROUP_CD = '073' AND (SELECT LA.LOC_CD FROM TB_LOCATION_ADM LA WHERE LA.LOC_NO = T2.LOCATION_NO) = X.BASE_CD) LOC_CD_NM , (SELECT X.BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_GROUP_CD = '067' AND (SELECT LA.AREA_CD FROM TB_LOCATION_ADM LA WHERE LA.LOC_NO = T2.LOCATION_NO) = X.BASE_CD) AREA_CD_NM , (SELECT LA.FLOOR_NM FROM TB_LOCATION_ADM LA WHERE LA.LOC_NO = T2.LOCATION_NO) FLOOR_NM , SUM(T2.SP_QTY) SP_QTY , T2.MAIN_GUBUN FROM ( SELECT T1.ITEM_SEQ , T1.ITEM_CD , T1.ITEM_NM , T1.ITEM_MODEL , ( CASE WHEN SPA.SP_GUBUN IN ('001', '003') THEN SPA.SP_QTY * '1' ELSE SPA.SP_QTY * (-1) END ) SP_QTY , SPA.LOCATION_NO , T1.MAIN_GUBUN FROM ( SELECT IIA.ITEM_SEQ , IIA.ITEM_CD , IIA.ITEM_NM , IIA.ITEM_MODEL , IIA.MAIN_GUBUN FROM TB_WORK_ORDER_MATRL WOM LEFT OUTER JOIN TB_ITEM_INFO_ADM IIA ON WOM.ITEM_SEQ = IIA.ITEM_SEQ WHERE 1=1
