AND WOM.WORK_ORD_NO IN (SELECT X.WORK_ORD_NO FROM TB_WORK_ORDER_MATRL X WHERE X.BARCODE_NO = #{barcodeNo})
AND WOM.WORK_ORD_NO = #{workOrdNo}
GROUP BY IIA.ITEM_SEQ, IIA.ITEM_CD, IIA.ITEM_NM, IIA.ITEM_MODEL, IIA.MAIN_GUBUN ) T1 LEFT OUTER JOIN TB_STOCK_PAYMENT_ADM SPA ON T1.ITEM_SEQ = SPA.SP_CD ) T2 GROUP BY T2.ITEM_SEQ, T2.ITEM_CD, T2.ITEM_NM, T2.ITEM_MODEL, T2.LOCATION_NO, T2.MAIN_GUBUN HAVING 1=1 AND SUM(T2.SP_QTY) > 0
AND T2.MAIN_GUBUN = #{mainGubun}

