SELECT IA.ITEM_WORKPLACE, (SELECT BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_CD=IA.ITEM_MODEL AND X.BASE_GROUP_CD='099') ITEM_MODEL, IA.ITEM_CD, IA.ITEM_NM, IA.ITEM_DESC, Z.ITEM_SEQ, Z.ORD_LOT_NO, Z.REG_DATE, Z.WORK_START_TIME, Z.WORK_END_TIME, DAY_TIME*24+((SECOND_TIME-(SECOND_TIME/86400)*86400)/3600) HOUR_TIME, ((SECOND_TIME-(SECOND_TIME/3600)*3600)/60) MIN_TIME, ((SECOND_TIME-(SECOND_TIME/60)*60)) SEC_TIME FROM (
SELECT WO.ITEM_SEQ, WP.ORD_LOT_NO, WO.REG_DATE, WP.WORK_START_TIME, WP.WORK_END_TIME, DATEDIFF(DAY,WO.REG_DATE,WP.WORK_START_TIME) DAY_TIME, DATEDIFF(SECOND,WO.REG_DATE,WP.WORK_START_TIME) SECOND_TIME FROM TB_WORK_ORDER_PRCSS WP LEFT OUTER JOIN TB_WORK_ORDER WO ON WP.WORK_ORD_NO=WO.WORK_ORD_NO WHERE 1=1 AND WP.WORK_STATUS = '002'
AND CONVERT(CHAR(8), WO.REG_DATE, 112) BETWEEN #{regDateFrom} AND #{regDateTo}
)Z LEFT OUTER JOIN TB_ITEM_INFO_ADM IA ON Z.ITEM_SEQ=IA.ITEM_SEQ ORDER BY REG_DATE ASC, WORK_START_TIME ASC, WORK_END_TIME ASC

SELECT A.LEAD_COUNT, CONCAT(A.HOUR_TIME,':',A.MIN_TIME,':',A.SEC_TIME) TOTAL_LEAD_TIME, CONCAT(A.HOUR_TIME_AVG,':',A.MIN_TIME_AVG,':',A.SEC_TIME_AVG) AVG_LEAD_TIME
FROM ( SELECT COUNT(*) LEAD_COUNT, SUM(DAY_TIME)*24+((SUM(SECOND_TIME)-(SUM(SECOND_TIME)/86400)*86400)/3600) HOUR_TIME, ((SUM(SECOND_TIME)-(SUM(SECOND_TIME)/3600)*3600)/60) MIN_TIME, ((SUM(SECOND_TIME)-(SUM(SECOND_TIME)/60)*60)) SEC_TIME, SUM(DAY_TIME)*24+(((SUM(SECOND_TIME)/COUNT(*))-(SUM(SECOND_TIME)/COUNT(*)/86400)*86400)/3600) HOUR_TIME_AVG, (((SUM(SECOND_TIME)/COUNT(*))-(SUM(SECOND_TIME)/COUNT(*)/3600)*3600)/60) MIN_TIME_AVG, (((SUM(SECOND_TIME)/COUNT(*))-(SUM(SECOND_TIME)/COUNT(*)/60)*60)) SEC_TIME_AVG FROM ( SELECT DATEDIFF(DAY,WO.REG_DATE,WP.WORK_START_TIME) DAY_TIME, DATEDIFF(SECOND,WO.REG_DATE,WP.WORK_START_TIME) SECOND_TIME FROM TB_WORK_ORDER_PRCSS WP LEFT OUTER JOIN TB_WORK_ORDER WO ON WP.WORK_ORD_NO=WO.WORK_ORD_NO WHERE 1=1 AND WP.WORK_STATUS = '002'
AND CONVERT(CHAR(8), WO.REG_DATE, 112) BETWEEN #{regDateFrom} AND #{regDateTo}
)Z )A

SELECT Z.WORK_ORD_NO, (SELECT BASE_NM FROM TB_COMMON_CODE_ADM X WHERE X.BASE_CD=IA.ITEM_MODEL AND X.BASE_GROUP_CD='099') ITEM_MODEL, IA.ITEM_CD, IA.ITEM_NM, Z.REG_DATE, Z.WORK_START_TIME, DAY_TIME * 24 + ((SECOND_TIME - (SECOND_TIME / 86400) * 86400) / 3600) HOUR_TIME, ((SECOND_TIME - (SECOND_TIME / 3600) * 3600) / 60) MIN_TIME, ((SECOND_TIME - (SECOND_TIME / 60) * 60)) SEC_TIME, Z.WORK_END_TIME, IA.ITEM_WORKPLACE, IA.ITEM_DESC, Z.ITEM_SEQ FROM ( SELECT WO.ITEM_SEQ, WP.WORK_ORD_NO, WO.REG_DATE, WP.WORK_START_TIME, WP.WORK_END_TIME, DATEDIFF(DAY, WO.REG_DATE, WP.WORK_START_TIME) DAY_TIME, DATEDIFF(SECOND, WO.REG_DATE, WP.WORK_START_TIME) SECOND_TIME FROM TB_WORK_ORDER_PRCSS WP LEFT OUTER JOIN TB_WORK_ORDER WO ON WP.WORK_ORD_NO = WO.WORK_ORD_NO INNER JOIN ( SELECT WP.WORK_ORD_NO , MIN(WP.WORK_ORD_SEQ) WORK_ORD_SEQ FROM TB_WORK_ORDER_PRCSS WP GROUP BY WP.WORK_ORD_NO ) M ON WP.WORK_ORD_NO = M.WORK_ORD_NO WHERE 1=1 AND WP.WORK_STATUS = '002' AND WP.WORK_ORD_SEQ = M.WORK_ORD_SEQ
AND CONVERT(CHAR(8), WO.REG_DATE, 112) BETWEEN #{regDateFrom} AND #{regDateTo}
)Z LEFT OUTER JOIN TB_ITEM_INFO_ADM IA ON Z.ITEM_SEQ = IA.ITEM_SEQ ORDER BY REG_DATE ASC, WORK_START_TIME ASC, WORK_END_TIME ASC
